    private static void babes3() throws JSONException, IOException {
	File ff = new File("C:\\PASimulator\\orderdatajsons\\PABF\\shipconfirm\\shipconfirmjson");
	Set<String> set = new HashSet<>();
	FileWriter fw = new FileWriter("C:\\PASimulator\\orderdatajsons\\PABF\\shipconfirm\\orderIds282.csv");
	for (File f : ff.listFiles()) {
	    JSONObject json = new JSONObject(Files.readAllLines(f.toPath()).stream().collect(Collectors.joining()));
	    String orderId = json.getJSONObject("Message").getJSONObject("ShipConfirm")
		    .getJSONObject("ShipConfirmDetails").getJSONArray("OriginalOrder").getJSONObject(0)
		    .getString("OriginalOrderId");
	    if (set.contains(orderId)) {
		f.delete();
	    } else {
		set.add(orderId);
	    }

	}
	for (String s : set) {
	    fw.write(s + "\n");
	}
	fw.close();
	System.out.println(set.size());
    }

